(5, 5, 'Image - Titre Description', '<div class=\"row\">\r\n\r\n<div class=\"col-md-6\">\r\n\r\n<input type=\"hidden\" name=\"{$NAME}\" value=\"{$VALUE0}\" class=\"champ-image-1\">\r\n<a title=\"Gestion image\" href=\"#advanceddescritpion_updateimg\" data-line=\"{$LINE}\" class=\"btn btn-primary btn-responsive action-btn btn-modal-img\" data-toggle=\"modal\" data-img=\"1\"><i class=\"glyphicon glyphicon-edit\"></i></a>\r\n{$BALISE_IMAGE1}\r\n\r\n</div>\r\n<div class=\"col-md-6\">\r\n<input type=\"text\" name=\"{$NAME}\" value=\"{$VALUE1}\" placeholder=\"Titre\" class=\"form-control\">\r\n\r\n<textarea name=\"{$NAME}\" placeholder=\"Description\" class=\"form-control wysiwyg\">{$VALUE2}</textarea>\r\n\r\n</div>\r\n</div>', '<div class=\"row mb-4 align-items-center {$CLASS0}\">\n\n<div class=\"col-md-{$REPARTITION_1} pr-img\">\n<img src=\"{$VALUE0}\" alt=\"{$TITLE}\" class=\"img-fluid img-responsive {$CLASS1}\">\n</div>\n<div class=\"col-md-{$REPARTITION_2}\">\n<div>\n<h{$OPTION} class=\"mt-0 {$CLASS2}\">{$VALUE1}</h{$OPTION}>\n<div class=\"description {$CLASS3}\">{$VALUE2}</div>\n</div>\n</div>\n</div>', NULL, '2020-04-05 10:00:00', '2020-04-05 10:00:00'),
(6, 6, 'Titre Description - Image', '<div class=\"row\">\r\n\r\n<div class=\"col-md-6\">\r\n<input type=\"text\" name=\"{$NAME}\" value=\"{$VALUE0}\" placeholder=\"Titre\" class=\"form-control\">\r\n<textarea name=\"{$NAME}\" placeholder=\"Description\" class=\"form-control wysiwyg\">{$VALUE1}</textarea>\r\n</div>\r\n\r\n<div class=\"col-md-6\">\r\n<input type=\"hidden\" name=\"{$NAME}\" value=\"{$VALUE2}\" class=\"champ-image-3\">\r\n<a title=\"Gestion image\" href=\"#advanceddescritpion_updateimg\" data-line=\"{$LINE}\" class=\"btn btn-primary btn-responsive action-btn btn-modal-img\" data-toggle=\"modal\" data-img=\"3\"><i class=\"glyphicon glyphicon-edit\"></i></a>\r\n{$BALISE_IMAGE3}\r\n\r\n\r\n</div>\r\n</div>', '<div class=\"row mb-4 align-items-center {$CLASS0}\">\n\n<div class=\"col-md-{$REPARTITION_1}\">\n<h{$OPTION} class=\"mt-0 {$CLASS1}\">{$VALUE0}</h{$OPTION}>\n<div class=\"description {$CLASS2}\">{$VALUE1}</div>\n</div>\n\n<div class=\"col-md-{$REPARTITION_2} pl-img\">\n<img src=\"{$VALUE2}\" alt=\"{$TITLE}\" class=\"img-fluid img-responsive {$CLASS3}\">\n</div>\n</div>', NULL, '2020-04-05 10:00:00', '2020-04-05 10:00:00'),
